//       name: "Product2",
//       price: 80
//   },{
//       id: 2,
//       name: "Product2",
//       price: 80
//   }
// ];



const products = [];

function addProducts(quantity) {
  const startId = products.length;
  for (let i = 0; i < quantity; i++) {
    const id = startId + i;
    products.push({
      id: id,
      name: 'Item name ' + id,
      price: 2100 + i
    });
  }
}

addProducts(5);


class CustomInsertModal extends React.Component {

  handleSaveBtnClick = () => {
    const { columns, onSave } = this.props;
    const newRow = {};
    columns.forEach((column, i) => {
      newRow[column.field] = this.refs[column.field].value;
    }, this);
    // You should call onSave function and give the new row
    onSave(newRow);
  }

  render() {
    const {
      onModalClose,
      onSave,
      columns,
      validateState,
      ignoreEditable
    } = this.props;


  }
}




export default class Projects extends Component {
  state = {
  }

  createCustomModal = (onModalClose, onSave, columns, validateState, ignoreEditable) => {
    const attr = {
      onModalClose, onSave, columns, validateState, ignoreEditable
    };
    return (
      <CustomInsertModal { ... attr } />
    );
  }


  render () {

     const options = {
     insertModal: this.createCustomModal
  };

      return (
        <div className="divProj">

          <div id="divUpProj">
            <div id="titleProj">
               <h1>Projects overview</h1>
            </div>
          </div>

          <div id="divDownProj">
            <div id="projTable">
               <BootstrapTable data={ products } insertRow style="overflow-y:scroll">
                 <TableHeaderColumn dataField='id' isKey>Product ID</TableHeaderColumn>
                 <TableHeaderColumn dataField='name'>Product Name</TableHeaderColumn>
                 <TableHeaderColumn dataField='price'>Product Price</TableHeaderColumn>
               </BootstrapTable>
            </div>
          </div>

        </div>
      )
   }

}
